        <form @submit.prevent="submit" class="text-[22px] mx-10">
            <div>
                <BreezeLabel for="name" class="text-black" value="Name" />
                <BreezeInput id="name" type="text" class="mt-1 border-b-8 border-[#639f1e] bg-transparent outline-0 block w-full" v-model="form.name" required autofocus autocomplete="name" />
            </div>

            <div class="mt-4">
                <BreezeLabel for="email" value="Email" />
                <BreezeInput id="email" type="email" class="mt-1 block w-full border-b-8 border-[#639f1e] outline-0 bg-transparent" v-model="form.email" required autocomplete="username" />
            </div>

            <div class="mt-4">
                <div class="flex mt-2 justify-between">
                    <BreezeLabel for="password" value="Password" />
                    <i @onclick="eye" class="fa fa-eye fa-lg mt-2 mr-4 text-[#639f1e]"></i>
                </div>
                <BreezeInput id="password" type="password" class="mt-1 block w-full border-b-8 border-[#639f1e] outline-0 bg-transparent" v-model="form.password" required autocomplete="new-password" />
            </div>

            <div class="mt-4">
                <div class="flex mt-2 justify-between">
                    <BreezeLabel for="password_confirmation" value="Confirm Password" />
                    <i @onclick="eye" class="fa fa-eye fa-lg mt-2 mr-4 text-[#639f1e]"></i>
                </div>
                <BreezeInput id="password_confirmation" type="password" class="mt-1 block w-full border-b-8 border-[#639f1e] outline-0 bg-transparent" v-model="form.password_confirmation" required autocomplete="new-password" />
            </div>
            <Link :href="route('login')" class="text-center flex justify-center mt-5 underline text-sm text-gray-600 hover:text-gray-900">
                Already registered?
            </Link>
            <div class="flex items-center justify-end mt-4">
                
                <BreezeButton class="bg-[#639f1e] text-black w-full font-sans submit mx-auto py-4 justify-center text-[30px] font-bold" :class="{ 'opacity-25': form.processing }" :disabled="form.processing">
                    Register
                </BreezeButton>
            </div>
        </form>
        </div>
                <footer class="fixed w-full bottom-0">
                    <div class="grid mx-auto text-[13.5px] sm:text-[20px] justify-center max-w-lg bg-white py-4 border border-black grid-cols-4">
                        <div class="text-center hover:text-[#639f1e] text-gray-900">
                            <i class="mx-auto fa-2x far fa-file-invoice"></i>
                            <div class="text-center">surveys</div>
                        </div>
                        <div class="text-center hover:text-[#639f1e] text-gray-900">
                            <i class="mx-auto fa-2x fal fa-lightbulb-on"></i>
                            <div class="text-center">projects</div>
                        </div>
                        <div class="text-center hover:text-[#639f1e] text-gray-900">
                            <i class="mx-auto fa-2x fal fa-user-friends"></i>
                            <div class="text-center">relationships</div>
                        </div>
                        <div class="text-center text-[#639f1e] hover:text-green-600">
                            <i class="mx-auto fa-2x fas fa-user-circle"></i>
                            <div class="text-center text-gray-700">accounts</div>
                        </div>
                    </div>
                </footer>
            </div>
    </BreezeGuestLayout>
